taskModel.script:
// define PhysObj class

function PhysObj (name) {
	this.name = name;
}

PhysObj.prototype.toString = function () { return this.name; }
function find (obj) { 
	return "PhysObj."+obj.name; 
}

PhysObj.DinnerPlate = new PhysObj("DinnerPlate");
PhysObj.SaladPlate = new PhysObj("SaladPlate");
PhysObj.SoupBowl = new PhysObj("SoupBowl");

PhysObj.Fork = new PhysObj("Fork");
PhysObj.Spoon = new PhysObj("Spoon");
PhysObj.Knife = new PhysObj("Knife");

PhysObj.WaterGlass = new PhysObj("WaterGlass");
PhysObj.WineGlass = new PhysObj("WineGlass");

PhysObj.SquareTable = new PhysObj("SquareTable");
