taskModel.script:

// define Location class
function Location (x,y,z) {
	this.x = x;
	this.y = y;
	this.z = z;
}
Location.prototype.toString = function () { return "["+this.x+","+this.y+","+this.z+"]"; }

// define PhysObj class

function PhysObj (name, location) {
	this.name = name;
	this.location = location;
}

PhysObj.prototype.toString = function () { return this.name; }
function find (obj) { 
	if(obj instanceof PhysObj)
		return "PhysObj."+obj.name; 
	else
		return "new Location("+obj.x+","+obj.y+","+obj.z+")";
}
function Reset(){
	PhysObj.DinnerPlate = new PhysObj("DinnerPlate", new Location(-10,-10,-10));
	PhysObj.SaladPlate = new PhysObj("SaladPlate", new Location(-10,-10,-10));
	PhysObj.SoupBowl = new PhysObj("SoupBowl", new Location(-10,-10,-10));

	PhysObj.Fork = new PhysObj("Fork", new Location(-10,-10,-10));
	PhysObj.Spoon = new PhysObj("Spoon", new Location(-10,-10,-10));
	PhysObj.Knife = new PhysObj("Knife", new Location(-10,-10,-10));

	PhysObj.WaterGlass = new PhysObj("WaterGlass", new Location(-10,-10,-10));
	PhysObj.WineGlass = new PhysObj("WineGlass", new Location(-10,-10,-10));

	PhysObj.SquareTable = new PhysObj("SquareTable", new Location(10,10,10)); 
}
Reset()
